import UIKit

protocol AppSearchPresentationLogic {
    func presentRecentHistory(response: AppSearch.RecentHitory.Response)
    func presentSearchWordHistory(response: AppSearch.SearchWordHitory.Response)
    func presentSearchAppStore(response: AppSearch.SearchAppStore.Response)
}

class AppSearchPresenter: AppSearchPresentationLogic {
    weak var viewController: AppSearchDisplayLogic?
    
    // MARK: Do something
    
    func presentRecentHistory(response: AppSearch.RecentHitory.Response) {
        let sectionModels = self.getRecentHistorySectionModel(recentHistoryModels: response.recentHistoryModels)
        let viewModel = AppSearch.RecentHitory.ViewModel(sectionModels: sectionModels)
        viewController?.displayRecentHistory(viewModel: viewModel)
    }
    
    func presentSearchWordHistory(response: AppSearch.SearchWordHitory.Response) {
        
        let sectionModels = self.getSearchWordHistorySectionModel(searchHistoryModel: response.searchHistoryModel)
        let viewModel = AppSearch.SearchWordHitory.ViewModel(sectionModels: sectionModels)
        viewController?.displaySearchWordHistory(viewModel: viewModel)
    }
    
    func presentSearchAppStore(response: AppSearch.SearchAppStore.Response) {
        let sectionModels = self.getSearchAppStoreSectionModel(appInfoModel: response.appInfoModels)
        let viewModel = AppSearch.SearchAppStore.ViewModel(sectionModels: sectionModels)
        viewController?.displaySearchAppStore(viewModel: viewModel)
    }
}

extension AppSearchPresenter {
    
    func getRecentHistorySectionModel(recentHistoryModels: [RecentHistoryModel]) -> [AppSearchBaseItemSection] {
        
        let recentWordTitle: [AppSearchBaseItemSection] = [
            AppSearchBaseItemSection(items: [
                AppSearchBaseItem(type: .recentWordTitle, object: RecentHistoryModel(searchWord: "최근 검색어", date: Date()))
                ])
        ]
        
        let maxCnt = 10
        let endIndex = recentHistoryModels.count >= maxCnt ? maxCnt : recentHistoryModels.count
        
        return recentWordTitle +
            recentHistoryModels[0..<endIndex]
                .compactMap{
                    AppSearchBaseItemSection(items: [
                        AppSearchBaseItem(type: .recentWordContent,
                                          object: $0)
                    ])}
    }
    
    func getSearchWordHistorySectionModel(searchHistoryModel: [SearchHistoryModel]) -> [AppSearchBaseItemSection] {
        
        return searchHistoryModel
            .compactMap{ AppSearchBaseItemSection(items: [
                AppSearchBaseItem(type: .searchWordList,
                                  object: $0)
            ])}
    }
    
    func getSearchAppStoreSectionModel(appInfoModel: [AppInfoModel]) -> [AppSearchBaseItemSection] {
        
        return appInfoModel
            .compactMap{ AppSearchBaseItemSection(items: [
                AppSearchBaseItem(type: .searchAppInfoList,
                                  object: $0)
            ])}
    }
}
